using System;using System.Collections;using System.Collections.Generic;using System.Linq;using UnityEngine;public class InjectOnly : Inject {	public override object GetFillValue(Type t) {		var generatedValuesOfType = Generator.FindCreatedObjectsOfType(t);		if (generatedValuesOfType.Count() != 1) {			throw new Exception($"Non-singular generation for type {t.GetNameCached()}: [{String.Join(", ", generatedValuesOfType.Select(x => x.ToString()))}]");		}		return generatedValuesOfType.Single();	}}